import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [message, setMessage] = useState('');

  const handleSearch = async () => {
    try {
      // Make a GET request to the backend
      const response = await axios.get('http://localhost:5001/api/fetch-data');
      setMessage(response.data.message); // Set the returned message in the state
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className="App">
      <h1>Test Backend Connection</h1>
      <button onClick={handleSearch}>Test Connection</button>
      <p>{message}</p>
    </div>
  );
};

export default App;


